import { useParams } from "react-router-dom";
import courses from "../data.js/courses";
const SingleCourse = () => {
  const params = useParams();
  const course = courses.find(course => course.slug === params.courseSlug);
  return (
    <>
      
      <h2>{course.title}</h2>
      <h3>{course.slug}</h3>
      <h4>{course.id}</h4>
    </>
  );
};

export default SingleCourse;
